            }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: '#fff' }}>
                {((selectedFeed.moisture / 100) * dailyAmount).toFixed(1)} г
              </div>
              <div style={{ fontSize: '12px', color: '#ddd' }}>Влага</div>
            </div>
          </div>
        </div>
      )}


      </div>
    </div>
  );
};

export default Calculator;
