import React from 'react';
import { Link, useLocation } from 'react-router-dom';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const location = useLocation();

  const navigation = [
    { name: 'Главная', href: '/', icon: '🏠' },
    { name: 'Животные', href: '/animals', icon: '🐕' },
    { name: 'Корма', href: '/feeds', icon: '🍖' },
    { name: 'Калькулятор', href: '/calculator', icon: '🧮' },
    { name: 'Сравнения', href: '/comparisons', icon: '📊' }
  ];

  return (
    <div style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f0f8f4 0%, #e8f5e8 100%)'
    }}>
      {/* Навигационная панель */}
      <nav style={{
        background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(240, 248, 244, 0.9) 100%)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(76, 175, 80, 0.2)',
        position: 'sticky',
        top: 0,
        zIndex: 1000,
        boxShadow: '0 4px 20px rgba(76, 175, 80, 0.1)'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: '0 20px',
          height: '70px'
        }}>
          {/* Логотип */}
          <Link 
            to="/" 
            style={{
              display: 'flex',
              alignItems: 'center',
              textDecoration: 'none',
              fontSize: '24px',
              fontWeight: '800',
              background: 'var(--primary-gradient)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'scale(1.05)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'scale(1)';
            }}
          >
            <span style={{ 
              fontSize: '32px', 
              marginRight: '12px',
              animation: 'float 3s ease-in-out infinite'
            }}>
              🏥
            </span>
            VetFormuLab
            <span style={{
              background: 'var(--accent-gradient)',
              color: 'white',
              padding: '4px 8px',
              borderRadius: '8px',
              fontSize: '10px',
              fontWeight: '600',
              marginLeft: '8px',
              animation: 'glow 2s ease-in-out infinite alternate'
            }}>
              PRO
            </span>
          </Link>

          {/* Навигационные ссылки */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            {navigation.map((item) => {
              const isActive = location.pathname === item.href;
              return (
                <Link
                  key={item.href}
                  to={item.href}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    padding: '12px 16px',
                    borderRadius: '12px',
                    textDecoration: 'none',
                    fontSize: '14px',
                    fontWeight: '600',
                    transition: 'all 0.3s ease',
                    background: isActive 
                      ? 'var(--primary-gradient)' 
                      : 'transparent',
                    color: isActive 
                      ? 'white' 
                      : 'var(--text-primary)',
                    border: isActive 
                      ? 'none' 
                      : '1px solid transparent'
                  }}
                  onMouseEnter={(e) => {
                    if (!isActive) {
                      e.currentTarget.style.background = 'rgba(76, 175, 80, 0.1)';
                      e.currentTarget.style.borderColor = 'rgba(76, 175, 80, 0.3)';
                      e.currentTarget.style.transform = 'translateY(-2px)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (!isActive) {
                      e.currentTarget.style.background = 'transparent';
                      e.currentTarget.style.borderColor = 'transparent';
                      e.currentTarget.style.transform = 'translateY(0)';
                    }
                  }}
                >
                  <span style={{ fontSize: '16px' }}>{item.icon}</span>
                  {item.name}
                </Link>
              );
            })}
          </div>

          {/* Правая панель */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '12px'
          }}>
            {/* Индикатор версии */}
            <div style={{
              display: 'flex',
              alignItems: 'center',
              background: 'rgba(76, 175, 80, 0.1)',
              color: 'var(--primary-green)',
              padding: '8px 12px',
              borderRadius: '20px',
              fontSize: '12px',
              fontWeight: '600',
              border: '1px solid rgba(76, 175, 80, 0.3)'
            }}>
              <span style={{ marginRight: '6px' }}>🌿</span>
              VET EDITION
            </div>

            {/* Кнопка помощи */}
            <button style={{
              background: 'var(--accent-gradient)',
              color: 'white',
              border: 'none',
              borderRadius: '50%',
              width: '40px',
              height: '40px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              cursor: 'pointer',
              fontSize: '16px',
              transition: 'all 0.3s ease',
              boxShadow: '0 4px 12px rgba(255, 143, 0, 0.3)'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'scale(1.1)';
              e.currentTarget.style.boxShadow = '0 6px 20px rgba(255, 143, 0, 0.4)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'scale(1)';
              e.currentTarget.style.boxShadow = '0 4px 12px rgba(255, 143, 0, 0.3)';
            }}
            >
              ❓
            </button>
          </div>
        </div>
      </nav>

      {/* Основной контент */}
      <main style={{
        minHeight: 'calc(100vh - 70px)',
        padding: '0'
      }}>
        {children}
      </main>

      {/* Футер */}
      <footer style={{
        background: 'linear-gradient(135deg, rgba(76, 175, 80, 0.05) 0%, rgba(139, 195, 74, 0.05) 100%)',
        borderTop: '1px solid rgba(76, 175, 80, 0.2)',
        padding: '40px 20px',
        textAlign: 'center'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto'
        }}>
          {/* Логотип в футере */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            marginBottom: '20px',
            fontSize: '20px',
            fontWeight: '700',
            color: 'var(--dark-green)'
          }}>
            <span style={{ fontSize: '24px', marginRight: '8px' }}>🏥🐾</span>
            VetFormuLab Pro
          </div>

          {/* Описание */}
          <p style={{
            color: 'var(--text-secondary)',
            marginBottom: '24px',
            fontSize: '14px',
            maxWidth: '500px',
            margin: '0 auto 24px auto'
          }}>
            Профессиональная система расчета и анализа кормов для здоровья ваших питомцев. 
            Основана на научных ветеринарных формулах и стандартах.
          </p>

          {/* Информация */}
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '30px',
            flexWrap: 'wrap',
            marginBottom: '20px'
          }}>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '6px',
              color: 'var(--text-secondary)',
              fontSize: '12px'
            }}>
              <span>🔬</span>
              Научный подход
            </div>
            <div style={{
              display: 'flex',
