import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  HomeIcon, 
  UserGroupIcon, 
  BeakerIcon, 
  ChartBarIcon, 
  CalculatorIcon,
  Bars3Icon,
  XMarkIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';

interface LayoutProps {
  children: React.ReactNode;
}

const navigation = [
  { name: 'Главная', href: '/', icon: HomeIcon },
  { name: 'Животные', href: '/animals', icon: UserGroupIcon },
  { name: 'Корма', href: '/feeds', icon: BeakerIcon },
  { name: 'Сравнение', href: '/comparison', icon: ChartBarIcon },
  { name: 'Калькулятор', href: '/calculator', icon: CalculatorIcon },
];

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const location = useLocation();

  const isActive = (href: string) => {
    if (href === '/') {
      return location.pathname === href;
    }
    return location.pathname.startsWith(href);
  };

  return (
    <div style={{ minHeight: '100vh', background: 'var(--bg-gradient)', display: 'flex' }}>
      {/* Мобильная overlay */}
      {sidebarOpen && (
        <div 
          style={{
            position: 'fixed',
            inset: 0,
            zIndex: 40,
            display: 'block'
          }}
          onClick={() => setSidebarOpen(false)}
        >
          <div style={{
            position: 'fixed',
            inset: 0,
            background: 'rgba(0, 0, 0, 0.5)',
            backdropFilter: 'blur(4px)'
          }} />
        </div>
      )}

      {/* Sidebar */}
      <div className={`sidebar ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      }`} style={{
        position: 'fixed',
        top: 0,
        left: 0,
        zIndex: 50,
        width: '280px',
        height: '100vh',
        transform: sidebarOpen ? 'translateX(0)' : 'translateX(-100%)',
        transition: 'transform 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      }}>
        <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
          {/* Logo */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            height: '80px',
            padding: '0 2rem',
            borderBottom: '1px solid rgba(102, 126, 234, 0.1)'
          }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{
                fontSize: '2rem',
                marginRight: '1rem',
                animation: 'pulse 2s infinite'
              }}>
                🐾
              </div>
              <div>
                <h1 style={{
                  fontSize: '1.5rem',
                  fontWeight: 800,
                  background: 'var(--primary-gradient)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text',
                  margin: 0
                }}>
                  VetFormuLab
                </h1>
                <p style={{
                  fontSize: '0.75rem',
                  color: 'var(--text-light)',
                  margin: 0,
                  fontWeight: 500,
                  letterSpacing: '0.5px'
                }}>
                  PRO EDITION
                </p>
              </div>
            </div>

            {/* Кнопка закрытия на мобильных */}
            <button
              type="button"
              style={{
                display: 'none',
                padding: '0.5rem',
                borderRadius: '8px',
                border: 'none',
                background: 'rgba(102, 126, 234, 0.1)',
                color: 'var(--text-dark)',
                cursor: 'pointer',
                transition: 'all 0.2s'
              }}
              onClick={() => setSidebarOpen(false)}
            >
              <XMarkIcon style={{ width: '1.5rem', height: '1.5rem' }} />
            </button>
          </div>

          {/* Navigation */}
          <nav style={{ flex: 1, padding: '2rem 1rem', display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
            {navigation.map((item) => {
              const Icon = item.icon;
              const active = isActive(item.href);

              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`nav-item ${active ? 'active' : ''}`}
                  onClick={() => setSidebarOpen(false)}
                >
                  <Icon style={{ width: '1.25rem', height: '1.25rem', marginRight: '0.75rem' }} />
                  <span style={{ fontWeight: 500 }}>{item.name}</span>
                  {active && <SparklesIcon style={{ width: '1rem', height: '1rem', marginLeft: 'auto' }} />}
                </Link>
              );
            })}
          </nav>

          {/* Footer */}
          <div style={{
            padding: '1.5rem',
            borderTop: '1px solid rgba(102, 126, 234, 0.1)',
            background: 'rgba(102, 126, 234, 0.05)'
          }}>
            <div style={{
              textAlign: 'center',
              fontSize: '0.75rem',
              color: 'var(--text-light)',
              lineHeight: 1.5
            }}>
              <div style={{ 
                fontWeight: 700, 
                background: 'var(--secondary-gradient)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                backgroundClip: 'text'
              }}>
                VetFormuLab v2.0
              </div>
              <div>Мировой стандарт расчета кормов</div>
              <div style={{ 
                marginTop: '0.5rem',
                padding: '0.25rem 0.5rem',
                background: 'var(--success-gradient)',
                borderRadius: '12px',
                color: 'white',
                fontSize: '0.7rem',
                fontWeight: 600
              }}>
                🚀 PREMIUM
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={{ 
        flex: 1, 
        display: 'flex', 
        flexDirection: 'column',
        marginLeft: '280px',
        minHeight: '100vh'
      }}>
        {/* Top bar */}
        <header style={{
          background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.9) 0%, rgba(255, 255, 255, 0.7) 100%)',
          backdropFilter: 'blur(20px)',
          borderBottom: '1px solid rgba(102, 126, 234, 0.1)',
          boxShadow: 'var(--shadow-lg)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            height: '80px',
            padding: '0 2rem'
          }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              {/* Кнопка мобильного меню */}
              <button
                type="button"
                style={{
                  display: 'none',
                  padding: '0.5rem',
                  borderRadius: '8px',
                  border: 'none',
                  background: 'rgba(102, 126, 234, 0.1)',
                  color: 'var(--text-dark)',
                  cursor: 'pointer',
                  marginRight: '1rem'
                }}
                onClick={() => setSidebarOpen(true)}
              >
                <Bars3Icon style={{ width: '1.5rem', height: '1.5rem' }} />
              </button>

              {/* Page title */}
              <div>
                <h2 style={{
                  fontSize: '1.5rem',
                  fontWeight: 700,
                  background: 'var(--primary-gradient)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text',
                  margin: 0
                }}>
                  {navigation.find(item => isActive(item.href))?.name || 'VetFormuLab Pro'}
                </h2>
              </div>
            </div>

            {/* User menu */}
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
              <div style={{
                padding: '0.5rem 1rem',
                background: 'var(--warning-gradient)',
                borderRadius: '20px',
                color: 'white',
                fontSize: '0.8rem',
                fontWeight: 600,
                animation: 'pulse 2s infinite'
              }}>
                🔥 PRO VERSION
              </div>

              <div style={{
                width: '40px',
                height: '40px',
                borderRadius: '50%',
                background: 'var(--primary-gradient)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontWeight: 700,
                fontSize: '1.2rem',
                cursor: 'pointer',
                transition: 'all 0.3s',
                boxShadow: 'var(--shadow-lg)'
              }}>
                👨‍⚕️
              </div>
            </div>
          </div>
        </header>

        {/* Page content */}
        <main style={{ 
          flex: 1, 
          overflow: 'auto',
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)'
        }}>
          <div className="animate-fadeIn" style={{ padding: '2rem' }}>
            {children}
          </div>
        </main>
      </div>
    </div>
  );
};

export default Layout;
