import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Register: React.FC = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    clinic_name: '',
    clinic_phone: '',
    clinic_address: '',
    contact_person: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);

  const { register } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');

    // Валидация
    if (formData.password !== formData.confirmPassword) {
      setError('Пароли не совпадают');
      return;
    }

    if (formData.password.length < 6) {
      setError('Пароль должен содержать минимум 6 символов');
      return;
    }

    setLoading(true);

    try {
      await register({
        email: formData.email,
        password: formData.password,
        clinic_name: formData.clinic_name,
        clinic_phone: formData.clinic_phone || undefined,
        clinic_address: formData.clinic_address || undefined,
        contact_person: formData.contact_person || undefined
      });
      navigate('/'); // Перенаправляем на главную после регистрации
    } catch (error: any) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const nextStep = () => {
    // Валидация первого шага
    if (currentStep === 1) {
      if (!formData.email || !formData.password || !formData.confirmPassword) {
        setError('Заполните все обязательные поля');
        return;
      }
      if (formData.password !== formData.confirmPassword) {
        setError('Пароли не совпадают');
        return;
      }
      if (formData.password.length < 6) {
        setError('Пароль должен содержать минимум 6 символов');
        return;
      }
    }
    setError('');
    setCurrentStep(2);
  };

  const prevStep = () => {
    setError('');
    setCurrentStep(1);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f0f8f4 0%, #e8f5e8 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px'
    }}>
      {/* Фоновые декоративные элементы */}
      <div style={{
        position: 'absolute',
        top: '15%',
        left: '5%',
        fontSize: '100px',
        opacity: 0.05,
        animation: 'float 8s ease-in-out infinite',
        userSelect: 'none'
      }}>
        🌱
      </div>
      <div style={{
        position: 'absolute',
        bottom: '10%',
        right: '10%',
        fontSize: '90px',
        opacity: 0.05,
        animation: 'float 5s ease-in-out infinite 1s',
        userSelect: 'none'
      }}>
        🐱
      </div>
      <div style={{
        position: 'absolute',
        top: '25%',
        right: '25%',
        fontSize: '70px',
        opacity: 0.05,
        animation: 'paw-bounce 4s ease-in-out infinite 2s',
        userSelect: 'none'
      }}>
        💊
      </div>

      <div style={{
        background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(240, 248, 244, 0.9) 100%)',
        backdropFilter: 'blur(20px)',
        borderRadius: '24px',
        padding: '40px',
        maxWidth: '550px',
        width: '100%',
        boxShadow: '0 20px 60px rgba(76, 175, 80, 0.15)',
        border: '1px solid rgba(76, 175, 80, 0.2)',
        animation: 'slideUp 0.8s ease-out'
      }}>
        {/* Заголовок */}
        <div style={{
          textAlign: 'center',
          marginBottom: '32px'
        }}>
          <div style={{
            fontSize: '64px',
            marginBottom: '16px',
            animation: 'bounce 2s infinite'
          }}>
            🏥✨
          </div>
          <h1 style={{
            fontSize: '28px',
            fontWeight: '800',
            background: 'var(--primary-gradient)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            marginBottom: '8px'
          }}>
            Регистрация ветклиники
          </h1>
          <p style={{
            color: 'var(--text-secondary)',
            fontSize: '14px',
            marginBottom: '16px'
          }}>
            Присоединяйтесь к VetFormuLab Pro
          </p>

          {/* Индикатор шагов */}
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '12px',
            marginBottom: '8px'
          }}>
            <div style={{
              width: '40px',
              height: '4px',
              borderRadius: '2px',
              background: currentStep >= 1 ? 'var(--primary-gradient)' : 'var(--light-green)',
              transition: 'all 0.3s ease'
            }} />
            <div style={{
              width: '40px',
              height: '4px',
              borderRadius: '2px',
              background: currentStep >= 2 ? 'var(--primary-gradient)' : 'var(--light-green)',
              transition: 'all 0.3s ease'
            }} />
          </div>
          <div style={{
            fontSize: '12px',
            color: 'var(--text-secondary)',
            fontWeight: '600'
          }}>
            Шаг {currentStep} из 2
          </div>
        </div>

        {/* Форма */}
        <form onSubmit={handleSubmit}>
          {error && (
            <div style={{
              background: 'rgba(244, 67, 54, 0.1)',
              color: '#F44336',
              padding: '12px 16px',
              borderRadius: '12px',
              marginBottom: '20px',
              fontSize: '14px',
              border: '1px solid rgba(244, 67, 54, 0.2)',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <span>⚠️</span>
              {error}
            </div>
          )}

          {/* Шаг 1: Учетные данные */}
          {currentStep === 1 && (
            <div style={{ animation: 'fadeIn 0.5s ease-out' }}>
              <h3 style={{
                color: 'var(--dark-green)',
                marginBottom: '20px',
                fontSize: '18px',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                🔐 Учетные данные
              </h3>

              {/* Email */}
              <div className="form-group">
                <label className="form-label" style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: 'var(--text-primary)',
                  fontWeight: '600',
                  marginBottom: '8px'
                }}>
                  <span>📧</span>
                  Email клиники *
                </label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="clinic@example.com"
                  required
                  style={{
                    fontSize: '16px',
                    padding: '14px 16px',
                    border: '2px solid var(--light-green)',
                    borderRadius: '12px',
                    transition: 'all 0.3s ease',
                    background: 'white'
                  }}
                />
              </div>

              {/* Пароль */}
              <div className="form-group">
                <label className="form-label" style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: 'var(--text-primary)',
                  fontWeight: '600',
                  marginBottom: '8px'
                }}>
                  <span>🔒</span>
                  Пароль *
                </label>
                <div style={{ position: 'relative' }}>
                  <input
                    type={showPassword ? 'text' : 'password'}
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Минимум 6 символов"
                    required
                    style={{
                      fontSize: '16px',
                      padding: '14px 50px 14px 16px',
                      border: '2px solid var(--light-green)',
                      borderRadius: '12px',
                      transition: 'all 0.3s ease',
                      background: 'white'
                    }}
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    style={{
                      position: 'absolute',
                      right: '12px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '18px',
                      color: 'var(--text-secondary)',
                      padding: '4px'
                    }}
                  >
                    {showPassword ? '🙈' : '👁️'}
                  </button>
                </div>
              </div>

              {/* Подтверждение пароля */}
              <div className="form-group">
                <label className="form-label" style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: 'var(--text-primary)',
                  fontWeight: '600',
                  marginBottom: '8px'
                }}>
                  <span>🔐</span>
                  Подтвердите пароль *
                </label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="Повторите пароль"
                  required
                  style={{
                    fontSize: '16px',
                    padding: '14px 16px',
                    border: '2px solid var(--light-green)',
                    borderRadius: '12px',
                    transition: 'all 0.3s ease',
                    background: 'white'
                  }}
                />
              </div>

              <button
                type="button"
                onClick={nextStep}
                style={{
                  width: '100%',
                  padding: '16px',
                  background: 'var(--primary-gradient)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '12px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '8px',
                  marginTop: '8px'
                }}
              >
                Далее 🏥 Информация о клинике
              </button>
            </div>
          )}

          {/* Шаг 2: Информация о клинике */}
          {currentStep === 2 && (
            <div style={{ animation: 'fadeIn 0.5s ease-out' }}>
              <h3 style={{
                color: 'var(--dark-green)',
                marginBottom: '20px',
                fontSize: '18px',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                🏥 Информация о клинике
              </h3>

              {/* Название клиники */}
              <div className="form-group">
                <label className="form-label" style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: 'var(--text-primary)',
                  fontWeight: '600',
                  marginBottom: '8px'
                }}>
                  <span>🏢</span>
                  Название клиники *
                </label>
                <input
                  type="text"
                  name="clinic_name"
                  value={formData.clinic_name}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="Ветеринарная клиника 'Здоровые лапки'"
                  required
                  style={{
                    fontSize: '16px',
                    padding: '14px 16px',
                    border: '2px solid var(--light-green)',
                    borderRadius: '12px',
                    transition: 'all 0.3s ease',
                    background: 'white'
                  }}
                />
              </div>

              {/* Контактное лицо */}
              <div className="form-group">
                <label className="form-label" style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: 'var(--text-primary)',
                  fontWeight: '600',
                  marginBottom: '8px'
                }}>
                  <span>👨‍⚕️</span>
                  Контактное лицо
                </label>
                <input
                  type="text"
                  name="contact_person"
                  value={formData.contact_person}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="Иванов Иван Иванович"
                  style={{
                    fontSize: '16px',
                    padding: '14px 16px',
                    border: '2px solid var(--light-green)',
                    borderRadius: '12px',
                    transition: 'all 0.3s ease',
                    background: 'white'
                  }}
                />
              </div>

              {/* Телефон */}
              <div className="form-group">
                <label className="form-label" style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: 'var(--text-primary)',
                  fontWeight: '600',
                  marginBottom: '8px'
                }}>
                  <span>📞</span>
                  Телефон клиники
                </label>
                <input
                  type="tel"
                  name="clinic_phone"
                  value={formData.clinic_phone}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="+7 (999) 123-45-67"
                  style={{
                    fontSize: '16px',
                    padding: '14px 16px',
                    border: '2px solid var(--light-green)',
                    borderRadius: '12px',
                    transition: 'all 0.3s ease',
                    background: 'white'
                  }}
                />
              </div>

              {/* Адрес */}
              <div className="form-group">
                <label className="form-label" style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  color: 'var(--text-primary)',
                  fontWeight: '600',
                  marginBottom: '8px'
                }}>
                  <span>📍</span>
                  Адрес клиники
                </label>
                <textarea
                  name="clinic_address"
                  value={formData.clinic_address}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="г. Москва, ул. Примерная, д. 123"
                  rows={3}
                  style={{
                    fontSize: '16px',
                    padding: '14px 16px',
                    border: '2px solid var(--light-green)',
                    borderRadius: '12px',
                    transition: 'all 0.3s ease',
                    background: 'white',
                    resize: 'vertical',
                    fontFamily: 'inherit'
                  }}
                />
              </div>

              {/* Кнопки навигации */}
              <div style={{
                display: 'flex',
                gap: '12px',
                marginTop: '8px'
              }}>
                <button
                  type="button"
                  onClick={prevStep}
                  style={{
                    flex: 1,
                    padding: '16px',
                    background: 'var(--warm-gradient)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '12px',
                    fontSize: '16px',
                    fontWeight: '600',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '8px'
                  }}
                >
                  ← Назад
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  style={{
                    flex: 2,
                    padding: '16px',
                    background: loading ? 'var(--light-green)' : 'var(--primary-gradient)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '12px',
                    fontSize: '16px',
                    fontWeight: '600',
                    cursor: loading ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s ease',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '8px',
                    opacity: loading ? 0.7 : 1
                  }}
                >
                  {loading ? (
                    <>
                      <div style={{
                        width: '20px',
                        height: '20px',
                        border: '2px solid rgba(255, 255, 255, 0.3)',
                        borderTop: '2px solid white',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite'
                      }} />
                      Регистрация...
                    </>
                  ) : (
                    <>
                      🚀 Создать клинику
                    </>
                  )}
                </button>
              </div>
            </div>
          )}
        </form>

        {/* Ссылка на вход */}
        <div style={{
          textAlign: 'center',
          marginTop: '32px',
          padding: '20px',
          background: 'rgba(76, 175, 80, 0.05)',
          borderRadius: '16px',
          border: '1px solid rgba(76, 175, 80, 0.1)'
        }}>
          <p style={{
            color: 'var(--text-secondary)',
            marginBottom: '12px',
            fontSize: '14px'
          }}>
            Уже есть аккаунт? 👋
          </p>
          <Link
            to="/login"
            style={{
              color: 'var(--primary-green)',
              textDecoration: 'none',
              fontWeight: '600',
              display: 'inline-flex',
              alignItems: 'center',
              gap: '6px',
              transition: 'all 0.3s ease'
            }}
          >
            🔑 Войти в систему
          </Link>
        </div>
      </div>

      {/* CSS анимации */}
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default Register;
