import React, { useState, useEffect } from 'react';

interface Feed {
  id: number;
  name: string;
  brand: string;
  type: string;
  metabolizable_energy: number;
  crude_protein: number;
  crude_fat: number;
  crude_fiber: number;
  calcium: number;
  phosphorus: number;
}

interface AnimalData {
  species: string;
  type: string;
  gender: string;
  age: number;
  condition: number;
  name: string;
  breed: string;
  status: string;
  activity: string;
  owner: string;
  currentWeight: number;
  targetWeight: number;
  adultWeight: number;
  lactationWeeks: number;
  contact: string;
  meCoefficient: number;
}

interface NutritionRequirements {
  proteinME: number;
  fatME: number;
  carbME: number;
  structure: {
    protein: number;
    fat: number;
    carbs: number;
    fiber: number;
  };
}

const Calculator: React.FC = () => {
  const [animalData, setAnimalData] = useState<AnimalData>({
    species: 'собака',
    type: 'вид животного',
    gender: 'самец',
    age: 3.5,
    condition: 6,
    name: 'Купер',
    breed: 'вольш корги пемброк',
    status: 'кастрированный',
    activity: 'склонность к ожирению',
    owner: '',
    currentWeight: 14.6,
    targetWeight: 13.14,
    adultWeight: 13.14,
    lactationWeeks: 0,
    contact: '',
    meCoefficient: 1.0
  });

  const [energyNeed, setEnergyNeed] = useState<number>(621.14);
  const [nutritionReq, setNutritionReq] = useState<NutritionRequirements>({
    proteinME: 35,
    fatME: 25,
    carbME: 40,
    structure: {
      protein: 4,
      fat: 4,
      carbs: 4,
      fiber: 4
    }
  });

  const [diagnosis, setDiagnosis] = useState('');
  const [intolerances, setIntolerances] = useState('');

  const [feeds, setFeeds] = useState<Feed[]>([]);
  const [selectedFeed, setSelectedFeed] = useState<Feed | null>(null);
  const [feedsLoading, setFeedsLoading] = useState(false);

  // Добавляю единые стили для полей
  const fieldStyle = {
    width: '100%',
    height: '48px',
    padding: '12px 16px',
    border: '2px solid #e2e8f0',
    borderRadius: '12px',
    fontSize: '14px',
    fontWeight: '500',
    background: '#ffffff',
    transition: 'all 0.2s ease',
    outline: 'none',
    appearance: 'none' as 'none',
    boxSizing: 'border-box' as 'border-box'
  };

  const focusStyle = {
    borderColor: '#00c851',
    boxShadow: '0 0 0 3px rgba(0, 200, 81, 0.1)'
  };

  const labelStyle = {
    fontSize: '12px',
    color: '#64748b',
    fontWeight: '500',
    marginTop: '4px',
    display: 'block'
  };

  // Расчет потребности в энергии
  const calculateEnergyNeed = () => {
    let rer = 70 * Math.pow(animalData.targetWeight, 0.75);

    // Коэффициенты активности
    let activityMultiplier = 1.0;

    if (animalData.status === 'кастрированный') {
      activityMultiplier = animalData.activity === 'склонность к ожирению' ? 1.2 : 1.4;
    } else if (animalData.status === 'интактный') {
      activityMultiplier = 1.6;
    } else if (animalData.status === 'беременность 1-4 недели') {
      activityMultiplier = 1.8;
    } else if (animalData.status === 'беременность >5 недель') {
      activityMultiplier = 2.0;
    } else if (animalData.status === 'лактация') {
      activityMultiplier = 2.0 + (0.25 * animalData.lactationWeeks);
    }

    const totalEnergy = rer * activityMultiplier * animalData.meCoefficient;
    setEnergyNeed(Math.round(totalEnergy * 100) / 100);
  };

  useEffect(() => {
    calculateEnergyNeed();
  }, [animalData]);

  useEffect(() => {
    fetchFeeds();
  }, []);

  const fetchFeeds = async () => {
    setFeedsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/feeds/detailed');
      const data = await response.json();
      if (data.success) {
        setFeeds(data.feeds);
      }
    } catch (error) {
      console.error('Error fetching feeds:', error);
    } finally {
      setFeedsLoading(false);
    }
  };

  const calculateDailyAmount = () => {
    if (!selectedFeed) return null;

    const dailyAmountGrams = (energyNeed / selectedFeed.metabolizable_energy) * 1000;
    const dailyAmountCups = dailyAmountGrams / 120;

    return {
      grams: Math.round(dailyAmountGrams * 10) / 10,
      cups: Math.round(dailyAmountCups * 10) / 10,
      protein: Math.round((selectedFeed.crude_protein * dailyAmountGrams / 100) * 10) / 10,
      fat: Math.round((selectedFeed.crude_fat * dailyAmountGrams / 100) * 10) / 10,
      calcium: Math.round((selectedFeed.calcium * dailyAmountGrams / 100) * 10) / 10,
      phosphorus: Math.round((selectedFeed.phosphorus * dailyAmountGrams / 100) * 10) / 10
    };
  };

