import React, { useState, useEffect } from 'react';

interface Feed {
  id: number;
  name: string;
  brand: string;
  type: string;
  metabolizable_energy: number;
  crude_protein: number;
  crude_fat: number;
  crude_fiber: number;
  calcium: number;
  phosphorus: number;
}

interface AnimalData {
  species: string;
  type: string;
  gender: string;
  age: number;
  condition: number;
  name: string;
  breed: string;
  status: string;
  activity: string;
  owner: string;
  currentWeight: number;
  targetWeight: number;
  adultWeight: number;
  lactationWeeks: number;
  contact: string;
  meCoefficient: number;
}

interface NutritionRequirements {
  proteinME: number;
  fatME: number;
  carbME: number;
  structure: {
    protein: number;
    fat: number;
    carbs: number;
    fiber: number;
  };
}

const Calculator: React.FC = () => {
  const [animalData, setAnimalData] = useState<AnimalData>({
    species: 'собака',
    type: 'вид животного',
    gender: 'самец',
    age: 3.5,
    condition: 6,
    name: 'Купер',
    breed: 'вольш корги пемброк',
    status: 'кастрированный',
    activity: 'склонность к ожирению',
    owner: '',
    currentWeight: 14.6,
    targetWeight: 13.14,
    adultWeight: 13.14,
    lactationWeeks: 0,
    contact: '',
    meCoefficient: 1.0
  });

  const [energyNeed, setEnergyNeed] = useState<number>(621.14);
  const [nutritionReq, setNutritionReq] = useState<NutritionRequirements>({
    proteinME: 35,
    fatME: 25,
    carbME: 40,
    structure: {
      protein: 4,
      fat: 4,
      carbs: 4,
      fiber: 4
    }
  });

  const [diagnosis, setDiagnosis] = useState('');
  const [intolerances, setIntolerances] = useState('');

  const [feeds, setFeeds] = useState<Feed[]>([]);
  const [selectedFeed, setSelectedFeed] = useState<Feed | null>(null);
  const [feedsLoading, setFeedsLoading] = useState(false);

  // Расчет потребности в энергии
  const calculateEnergyNeed = () => {
    let rer = 70 * Math.pow(animalData.targetWeight, 0.75);

    // Коэффициенты активности
    let activityMultiplier = 1.0;

    if (animalData.status === 'кастрированный') {
      activityMultiplier = animalData.activity === 'склонность к ожирению' ? 1.2 : 1.4;
    } else if (animalData.status === 'интактный') {
      activityMultiplier = 1.6;
    } else if (animalData.status === 'беременность 1-4 недели') {
      activityMultiplier = 1.8;
    } else if (animalData.status === 'беременность >5 недель') {
      activityMultiplier = 2.0;
    } else if (animalData.status === 'лактация') {
      activityMultiplier = 2.0 + (0.25 * animalData.lactationWeeks);
    }

    const totalEnergy = rer * activityMultiplier * animalData.meCoefficient;
    setEnergyNeed(Math.round(totalEnergy * 100) / 100);
  };

  useEffect(() => {
    calculateEnergyNeed();
  }, [animalData]);

  useEffect(() => {
    fetchFeeds();
  }, []);

  const fetchFeeds = async () => {
    setFeedsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/feeds/detailed');
      const data = await response.json();
      if (data.success) {
        setFeeds(data.feeds);
      }
    } catch (error) {
      console.error('Error fetching feeds:', error);
    } finally {
      setFeedsLoading(false);
    }
  };

  const calculateDailyAmount = () => {
    if (!selectedFeed) return null;

    const dailyAmountGrams = (energyNeed / selectedFeed.metabolizable_energy) * 1000;
    const dailyAmountCups = dailyAmountGrams / 120;

    return {
      grams: Math.round(dailyAmountGrams * 10) / 10,
      cups: Math.round(dailyAmountCups * 10) / 10,
      protein: Math.round((selectedFeed.crude_protein * dailyAmountGrams / 100) * 10) / 10,
      fat: Math.round((selectedFeed.crude_fat * dailyAmountGrams / 100) * 10) / 10,
      calcium: Math.round((selectedFeed.calcium * dailyAmountGrams / 100) * 10) / 10,
      phosphorus: Math.round((selectedFeed.phosphorus * dailyAmountGrams / 100) * 10) / 10
    };
  };

  const handleInputChange = (field: keyof AnimalData, value: any) => {
    setAnimalData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const clearData = () => {
    setAnimalData({
      species: 'собака',
      type: 'вид животного',
      gender: 'самец',
      age: 0,
      condition: 5,
      name: '',
      breed: '',
      status: 'кастрированный',
      activity: 'нормальная активность',
      owner: '',
      currentWeight: 0,
      targetWeight: 0,
      adultWeight: 0,
      lactationWeeks: 0,
      contact: '',
      meCoefficient: 1.0
    });
    setDiagnosis('');
    setIntolerances('');
  };

  return (
    <div style={{
      padding: '20px',
      maxWidth: '1400px',
      margin: '0 auto',
      background: 'rgba(255, 255, 255, 0.95)',
      borderRadius: '20px',
      boxShadow: '0 8px 32px rgba(0, 200, 81, 0.1)'
    }}>
      <h1 style={{
        textAlign: 'center',
        fontSize: '2.5rem',
        fontWeight: '700',
        color: '#2d3748',
        marginBottom: '40px',
        background: 'linear-gradient(135deg, #00C851 0%, #33B5E5 100%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent'
      }}>
        ВВОД ДАННЫХ
      </h1>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: '20px',
        marginBottom: '30px'
      }}>
        {/* Левая колонка - основные данные */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '15px',
          border: '2px solid rgba(0, 200, 81, 0.1)'
        }}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
            <div>
              <input
                type="text"
                value={animalData.species}
                onChange={(e) => handleInputChange('species', e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
                placeholder="собака"
              />
              <label style={{ fontSize: '12px', color: '#666' }}>вид животного</label>
            </div>

            <div>
              <select
                value={animalData.gender}
                onChange={(e) => handleInputChange('gender', e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              >
                <option value="самец">самец</option>
                <option value="самка">самка</option>
              </select>
              <label style={{ fontSize: '12px', color: '#666' }}>пол</label>
            </div>

            <div>
              <input
                type="text"
                value={animalData.breed}
                onChange={(e) => handleInputChange('breed', e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
                placeholder="порода"
              />
              <label style={{ fontSize: '12px', color: '#666' }}>порода</label>
            </div>

            <div>
              <input
                type="number"
                step="0.1"
                value={animalData.age}
                onChange={(e) => handleInputChange('age', parseFloat(e.target.value))}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              />
              <label style={{ fontSize: '12px', color: '#666' }}>возраст (лет)</label>
            </div>

            <div>
              <input
                type="number"
                min="1"
                max="9"
                value={animalData.condition}
                onChange={(e) => handleInputChange('condition', parseInt(e.target.value))}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              />
              <label style={{ fontSize: '12px', color: '#666' }}>кондиция (1-9)</label>
            </div>

            <div>
              <input
                type="text"
                value={animalData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
                placeholder="кличка"
              />
              <label style={{ fontSize: '12px', color: '#666' }}>кличка</label>
            </div>

            <div>
              <select
                value={animalData.status}
                onChange={(e) => handleInputChange('status', e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              >
                <option value="кастрированный">кастрированный</option>
                <option value="интактный">интактный</option>
                <option value="беременность 1-4 недели">беременность 1-4 недели</option>
                <option value="беременность >5 недель">беременность {'>'}5 недель</option>
                <option value="лактация">лактация</option>
              </select>
              <label style={{ fontSize: '12px', color: '#666' }}>статус</label>
            </div>

            <div>
              <select
                value={animalData.activity}
                onChange={(e) => handleInputChange('activity', e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              >
                <option value="склонность к ожирению">склонность к ожирению</option>
                <option value="нормальная активность">нормальная активность</option>
                <option value="высокая активность">высокая активность</option>
              </select>
              <label style={{ fontSize: '12px', color: '#666' }}>активность</label>
            </div>

            <div>
              <input
                type="text"
                value={animalData.owner}
                onChange={(e) => handleInputChange('owner', e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
                placeholder="владелец"
              />
              <label style={{ fontSize: '12px', color: '#666' }}>владелец</label>
            </div>
          </div>
        </div>

        {/* Средняя колонка - вес и расчеты */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '15px',
          border: '2px solid rgba(0, 200, 81, 0.1)'
        }}>
          <div style={{ marginBottom: '20px' }}>
            <h3 style={{ color: '#2d3748', marginBottom: '15px' }}>Вес</h3>
            <div style={{ display: 'grid', gap: '10px' }}>
              <div>
                <input
                  type="number"
                  step="0.1"
                  value={animalData.currentWeight}
                  onChange={(e) => handleInputChange('currentWeight', parseFloat(e.target.value))}
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid #FFB6B6',
                    borderRadius: '8px',
                    fontSize: '14px'
                  }}
                />
                <label style={{ fontSize: '12px', color: '#666' }}>текущий (кг)</label>
              </div>

              <div>
                <input
                  type="number"
                  step="0.1"
                  value={animalData.targetWeight}
                  onChange={(e) => handleInputChange('targetWeight', parseFloat(e.target.value))}
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid #FFB6B6',
                    borderRadius: '8px',
                    fontSize: '14px'
                  }}
                />
                <label style={{ fontSize: '12px', color: '#666' }}>расчетный (кг)</label>
              </div>

              <div>
                <input
                  type="number"
                  step="0.1"
                  value={animalData.adultWeight}
                  onChange={(e) => handleInputChange('adultWeight', parseFloat(e.target.value))}
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid #FFB6B6',
                    borderRadius: '8px',
                    fontSize: '14px'
                  }}
                />
                <label style={{ fontSize: '12px', color: '#666' }}>взрослый (кг)</label>
              </div>
            </div>
          </div>

          {animalData.status === 'лактация' && (
            <div style={{ marginBottom: '20px' }}>
              <input
                type="number"
                value={animalData.lactationWeeks}
                onChange={(e) => handleInputChange('lactationWeeks', parseInt(e.target.value))}
                style={{
                  width: '100%',
                  padding: '10px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              />
              <label style={{ fontSize: '12px', color: '#666' }}>недели лактации</label>
            </div>
          )}

          <div>
            <input
              type="text"
              value={animalData.contact}
              onChange={(e) => handleInputChange('contact', e.target.value)}
              style={{
                width: '100%',
                padding: '10px',
                border: '2px solid #FFB6B6',
                borderRadius: '8px',
                fontSize: '14px'
              }}
              placeholder="контакт"
            />
            <label style={{ fontSize: '12px', color: '#666' }}>контакт</label>
          </div>

          <div style={{ marginTop: '15px' }}>
            <input
              type="number"
              step="0.1"
              value={animalData.meCoefficient}
              onChange={(e) => handleInputChange('meCoefficient', parseFloat(e.target.value))}
              style={{
                width: '100%',
                padding: '10px',
                border: '2px solid #FFB6B6',
                borderRadius: '8px',
                fontSize: '14px'
              }}
            />
            <label style={{ fontSize: '12px', color: '#666' }}>коэффициент МЭ</label>
          </div>
        </div>

        {/* Правая колонка - результат и рацион */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '15px',
          border: '2px solid rgba(0, 200, 81, 0.1)'
        }}>
          <div style={{
            background: '#FFB6B6',
            padding: '15px',
            borderRadius: '10px',
            textAlign: 'center',
            marginBottom: '20px'
          }}>
            <div style={{ fontSize: '12px', color: '#666', marginBottom: '5px' }}>
              потребность в МЭ, ккал
            </div>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#2d3748' }}>
              {energyNeed}
            </div>
          </div>

          <button
            onClick={clearData}
            style={{
              width: '100%',
              padding: '10px',
              background: '#f0f0f0',
              border: '1px solid #ccc',
              borderRadius: '8px',
              fontSize: '14px',
              cursor: 'pointer',
              marginBottom: '20px'
            }}
          >
            очистить данные
          </button>

          <h4 style={{ color: '#2d3748', marginBottom: '15px' }}>Рацион</h4>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', marginBottom: '15px' }}>
            <div>
              <input
                type="number"
                value={nutritionReq.proteinME}
                onChange={(e) => setNutritionReq(prev => ({ ...prev, proteinME: parseInt(e.target.value) }))}
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              />
              <label style={{ fontSize: '12px', color: '#666' }}>МЭ белка, %</label>
            </div>

            <div>
              <input
                type="number"
                value={nutritionReq.fatME}
                onChange={(e) => setNutritionReq(prev => ({ ...prev, fatME: parseInt(e.target.value) }))}
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              />
              <label style={{ fontSize: '12px', color: '#666' }}>МЭ жира, %</label>
            </div>

            <div>
              <input
                type="number"
                value={nutritionReq.carbME}
                onChange={(e) => setNutritionReq(prev => ({ ...prev, carbME: parseInt(e.target.value) }))}
                style={{
                  width: '100%',
                  padding: '8px',
                  border: '2px solid #FFB6B6',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              />
              <label style={{ fontSize: '12px', color: '#666' }}>МЭ углеводов, %</label>
            </div>

            <div style={{ display: 'flex', alignItems: 'center', fontSize: '14px', color: '#666' }}>
              Структура г/100 ккал
            </div>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <h4 style={{ color: '#2d3748', marginBottom: '10px' }}>Параметры</h4>
            <textarea
              placeholder="новые ингредиенты"
              style={{
                width: '100%',
                padding: '10px',
                border: '2px solid #FFB6B6',
                borderRadius: '8px',
                fontSize: '14px',
                minHeight: '60px',
                marginBottom: '10px'
              }}
            />

            <textarea
              placeholder="снижение веса"
              style={{
                width: '100%',
                padding: '10px',
                border: '2px solid #FFB6B6',
                borderRadius: '8px',
                fontSize: '14px',
                minHeight: '60px'
              }}
            />
          </div>

          <div>
            <h4 style={{ color: '#2d3748', marginBottom: '10px' }}>Основной диагноз</h4>
            <textarea
              value={diagnosis}
              onChange={(e) => setDiagnosis(e.target.value)}
              placeholder="хроническая энтеропатия"
              style={{
                width: '100%',
                padding: '10px',
                border: '2px solid #FFB6B6',
                borderRadius: '8px',
                fontSize: '14px',
                minHeight: '60px',
                marginBottom: '15px'
              }}
            />

            <h4 style={{ color: '#2d3748', marginBottom: '10px' }}>Непереносимость продуктов</h4>
            <textarea
              value={intolerances}
              onChange={(e) => setIntolerances(e.target.value)}
              style={{
                width: '100%',
                padding: '10px',
                border: '2px solid #FFB6B6',
                borderRadius: '8px',
                fontSize: '14px',
                minHeight: '60px'
              }}
            />
          </div>
        </div>

        {/* Блок выбора корма */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '15px',
          border: '2px solid rgba(0, 200, 81, 0.1)'
        }}>
          <h3 style={{ color: '#2d3748', marginBottom: '15px', fontSize: '1.3rem', fontWeight: '600' }}>
            🍖 Выбор корма
          </h3>

          {feedsLoading ? (
            <div style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
              Загрузка кормов...
            </div>
          ) : (
            <div>
              <select
                value={selectedFeed?.id || ''}
                onChange={(e) => {
                  const feedId = parseInt(e.target.value);
                  const feed = feeds.find(f => f.id === feedId);
                  setSelectedFeed(feed || null);
                }}
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '2px solid rgba(0, 200, 81, 0.2)',
                  borderRadius: '8px',
                  fontSize: '14px',
                  marginBottom: '15px'
                }}
              >
                <option value="">Выберите корм для расчета суточной нормы</option>
                {feeds.map(feed => (
                  <option key={feed.id} value={feed.id}>
                    {feed.brand} - {feed.name} (МЭ: {feed.metabolizable_energy} ккал/кг)
                  </option>
                ))}
              </select>

              {selectedFeed && (
                <div style={{
                  background: 'linear-gradient(135deg, rgba(0, 200, 81, 0.05) 0%, rgba(51, 181, 229, 0.05) 100%)',
                  border: '2px solid rgba(0, 200, 81, 0.1)',
                  borderRadius: '12px',
                  padding: '15px',
                  marginBottom: '15px'
                }}>
                  <h4 style={{ color: '#2d3748', marginBottom: '10px', fontSize: '1.1rem' }}>
                    Информация о корме
                  </h4>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: '1fr 1fr 1fr',
                    gap: '10px',
                    fontSize: '14px'
                  }}>
                    <div>
                      <strong>Бренд:</strong> {selectedFeed.brand}
                    </div>
                    <div>
                      <strong>Тип:</strong> {selectedFeed.type}
                    </div>
                    <div>
                      <strong>МЭ:</strong> {selectedFeed.metabolizable_energy} ккал/кг
                    </div>
                    <div>
                      <strong>Белок:</strong> {selectedFeed.crude_protein}%
                    </div>
                    <div>
                      <strong>Жир:</strong> {selectedFeed.crude_fat}%
                    </div>
                    <div>
                      <strong>Клетчатка:</strong> {selectedFeed.crude_fiber}%
                    </div>
                  </div>
                </div>
              )}

              {selectedFeed && (() => {
                const dailyData = calculateDailyAmount();
                return dailyData ? (
                  <div style={{
                    background: '#00C851',
                    color: 'white',
                    padding: '20px',
                    borderRadius: '12px',
                    textAlign: 'center'
                  }}>
                    <h4 style={{ marginBottom: '15px', fontSize: '1.2rem' }}>
                      📊 Суточная норма
                    </h4>

                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: '1fr 1fr',
                      gap: '15px',
                      marginBottom: '15px'
                    }}>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.2)',
                        padding: '15px',
                        borderRadius: '10px'
                      }}>
                        <div style={{ fontSize: '1.8rem', fontWeight: '700' }}>
                          {dailyData.grams} г
                        </div>
                        <div style={{ fontSize: '12px', opacity: 0.9 }}>
                          в день
                        </div>
                      </div>

                      <div style={{
                        background: 'rgba(255, 255, 255, 0.2)',
                        padding: '15px',
                        borderRadius: '10px'
                      }}>
                        <div style={{ fontSize: '1.8rem', fontWeight: '700' }}>
                          {dailyData.cups} чашек
                        </div>
                        <div style={{ fontSize: '12px', opacity: 0.9 }}>
                          (120г/чашка)
                        </div>
                      </div>
                    </div>

                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: '1fr 1fr 1fr 1fr',
                      gap: '10px',
                      fontSize: '12px'
                    }}>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.15)',
                        padding: '10px',
                        borderRadius: '8px'
                      }}>
                        <div style={{ fontWeight: '600' }}>Белок</div>
                        <div>{dailyData.protein} г</div>
                      </div>

                      <div style={{
                        background: 'rgba(255, 255, 255, 0.15)',
                        padding: '10px',
                        borderRadius: '8px'
                      }}>
                        <div style={{ fontWeight: '600' }}>Жир</div>
                        <div>{dailyData.fat} г</div>
                      </div>

                      <div style={{
                        background: 'rgba(255, 255, 255, 0.15)',
                        padding: '10px',
                        borderRadius: '8px'
                      }}>
                        <div style={{ fontWeight: '600' }}>Ca</div>
                        <div>{dailyData.calcium} г</div>
                      </div>

                      <div style={{
                        background: 'rgba(255, 255, 255, 0.15)',
                        padding: '10px',
                        borderRadius: '8px'
                      }}>
                        <div style={{ fontWeight: '600' }}>P</div>
                        <div>{dailyData.phosphorus} г</div>
                      </div>
                    </div>
                  </div>
                ) : null;
              })()}
            </div>
          )}
        </div>
      </div>

      {/* Формулы внизу */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.8)',
        padding: '20px',
        borderRadius: '15px',
        border: '2px solid rgba(0, 200, 81, 0.1)',
        marginTop: '20px'
      }}>
        <h3 style={{ color: '#2d3748', marginBottom: '20px' }}>Формулы</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px', fontSize: '14px' }}>
          <div><strong>собака интактный:</strong> 1 - 95 ккал/кг - 1.0</div>
          <div><strong>кошка кастрированный:</strong> 2 - 110 ккал/кг - 1.1</div>
          <div><strong>хорек беременность 1-4 недели:</strong> 3 - взрослая собака - 1.2</div>
          <div><strong>беременность >5 недель:</strong> 4 - очень высокая, экстрем. усл. - поздний рост - 1.3</div>
          <div><strong>лактация:</strong> 5 - лактация - 1.4</div>
          <div><strong>молодые:</strong> 6 - 100 ккал/кг рост - 1.5</div>
          <div><strong>пожилой кот:</strong> 7 - рост - 1.6</div>
          <div><strong>самец:</strong> 8 - - 1.7</div>
          <div><strong>самка:</strong> 9 - - 1.8</div>
        </div>
      </div>
    </div>
  );
};

export default Calculator;
