import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Login: React.FC = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await login(formData.email, formData.password);
      navigate('/'); // Перенаправляем на главную после входа
    } catch (error: any) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'var(--hero-gradient)',
      backgroundSize: '400% 400%',
      animation: 'gradient-shift 8s ease infinite',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Фоновые декоративные элементы */}
      <div style={{
        position: 'absolute',
        top: '10%',
        left: '10%',
        fontSize: '120px',
        opacity: 0.05,
        animation: 'float 6s ease-in-out infinite',
        userSelect: 'none'
      }}>
        🌿
      </div>
      <div style={{
        position: 'absolute',
        bottom: '15%',
        right: '15%',
        fontSize: '80px',
        opacity: 0.05,
        animation: 'float 4s ease-in-out infinite 2s',
        userSelect: 'none'
      }}>
        🐕
      </div>
      <div style={{
        position: 'absolute',
        top: '20%',
        right: '20%',
        fontSize: '60px',
        opacity: 0.05,
        animation: 'paw-bounce 3s ease-in-out infinite 1s',
        userSelect: 'none'
      }}>
        🏥
      </div>

      <div style={{
        background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(240, 248, 244, 0.9) 100%)',
        backdropFilter: 'blur(20px)',
        borderRadius: '24px',
        padding: '40px',
        maxWidth: '480px',
        width: '100%',
        boxShadow: '0 20px 60px rgba(76, 175, 80, 0.15)',
        border: '1px solid rgba(76, 175, 80, 0.2)',
        animation: 'slideUp 0.8s ease-out'
      }}>
        {/* Заголовок */}
        <div style={{
          textAlign: 'center',
          marginBottom: '40px'
        }}>
          <div style={{
            fontSize: '64px',
            marginBottom: '16px',
            animation: 'bounce 2s infinite'
          }}>
            🏥🐾
          </div>
          <h1 style={{
            fontSize: '32px',
            fontWeight: '800',
            background: 'var(--primary-gradient)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            marginBottom: '8px'
          }}>
            VetFormuLab Pro
          </h1>
          <p style={{
            color: 'var(--text-secondary)',
            fontSize: '16px',
            marginBottom: '16px'
          }}>
            Вход в систему для ветеринарных клиник
          </p>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            background: 'rgba(76, 175, 80, 0.1)',
            color: 'var(--primary-green)',
            padding: '6px 12px',
            borderRadius: '16px',
            fontSize: '12px',
            fontWeight: '600',
            border: '1px solid rgba(76, 175, 80, 0.3)'
          }}>
            <span style={{ marginRight: '6px' }}>🌿</span>
            Профессиональная версия
          </div>
        </div>

        {/* Форма */}
        <form onSubmit={handleSubmit}>
          {error && (
            <div style={{
              background: 'rgba(244, 67, 54, 0.1)',
              color: '#F44336',
              padding: '12px 16px',
              borderRadius: '12px',
              marginBottom: '20px',
              fontSize: '14px',
              border: '1px solid rgba(244, 67, 54, 0.2)',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <span>⚠️</span>
              {error}
            </div>
          )}

          {/* 📧 Email с FLOATING LABEL */}
          <div style={{ position: 'relative', marginBottom: '24px' }}>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              style={{
                width: '100%',
                fontSize: '16px',
                padding: '20px 16px 12px 50px',
                border: '2px solid rgba(255, 255, 255, 0.2)',
                borderRadius: '16px',
                transition: 'all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94)',
                background: 'rgba(255, 255, 255, 0.9)',
                backdropFilter: 'blur(10px)',
                outline: 'none',
                color: 'var(--text-primary)',
                boxShadow: '0 8px 25px rgba(0,0,0,0.1)'
              }}
              onFocus={(e) => {
                e.target.style.borderColor = 'var(--primary-green)';
                e.target.style.boxShadow = '0 12px 35px rgba(0, 200, 81, 0.2)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onBlur={(e) => {
                e.target.style.borderColor = 'rgba(255, 255, 255, 0.2)';
                e.target.style.boxShadow = '0 8px 25px rgba(0,0,0,0.1)';
                e.target.style.transform = 'translateY(0)';
              }}
            />
            <label style={{
              position: 'absolute',
              left: '50px',
              top: formData.email ? '8px' : '50%',
              transform: formData.email ? 'translateY(0)' : 'translateY(-50%)',
              fontSize: formData.email ? '12px' : '16px',
              fontWeight: '600',
              color: formData.email ? 'var(--primary-green)' : 'var(--text-light)',
              transition: 'all 0.3s ease',
              pointerEvents: 'none',
              background: formData.email ? 'rgba(255,255,255,0.9)' : 'transparent',
              padding: formData.email ? '0 8px' : '0',
              borderRadius: '8px'
            }}>
              Email клиники
            </label>
            <span style={{
              position: 'absolute',
              left: '16px',
              top: '50%',
              transform: 'translateY(-50%)',
              fontSize: '20px',
              color: 'var(--primary-green)',
              animation: 'float 3s ease-in-out infinite'
            }}>
              📧
            </span>
          </div>

          {/* Пароль */}
          <div className="form-group">
            <label className="form-label" style={{
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
              color: 'var(--text-primary)',
              fontWeight: '600',
              marginBottom: '8px'
            }}>
              <span>🔒</span>
              Пароль
            </label>
            <div style={{ position: 'relative' }}>
              <input
                type={showPassword ? 'text' : 'password'}
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="form-control"
                placeholder="••••••••"
                required
                style={{
                  fontSize: '16px',
                  padding: '14px 50px 14px 16px',
                  border: '2px solid var(--light-green)',
                  borderRadius: '12px',
                  transition: 'all 0.3s ease',
                  background: 'white'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = 'var(--primary-green)';
                  e.target.style.boxShadow = '0 0 0 3px rgba(76, 175, 80, 0.1)';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = 'var(--light-green)';
                  e.target.style.boxShadow = 'none';
                }}
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                style={{
                  position: 'absolute',
                  right: '12px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '18px',
                  color: 'var(--text-secondary)',
                  padding: '4px'
                }}
              >
                {showPassword ? '🙈' : '👁️'}
              </button>
            </div>
          </div>

          {/* Кнопка входа */}
          <button
            type="submit"
            disabled={loading}
            style={{
              width: '100%',
              padding: '16px',
              background: loading ? 'var(--light-green)' : 'var(--primary-gradient)',
              color: 'white',
              border: 'none',
              borderRadius: '12px',
              fontSize: '16px',
              fontWeight: '600',
              cursor: loading ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s ease',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px',
              marginTop: '8px',
              opacity: loading ? 0.7 : 1
            }}
            onMouseEnter={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(-2px)';
                e.currentTarget.style.boxShadow = '0 8px 24px rgba(76, 175, 80, 0.4)';
              }
            }}
            onMouseLeave={(e) => {
              if (!loading) {
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = '0 4px 16px rgba(76, 175, 80, 0.3)';
              }
            }}
          >
            {loading ? (
              <>
                <div style={{
                  width: '20px',
                  height: '20px',
                  border: '2px solid rgba(255, 255, 255, 0.3)',
                  borderTop: '2px solid white',
                  borderRadius: '50%',
                  animation: 'spin 1s linear infinite'
                }} />
                Вход в систему...
              </>
            ) : (
              <>
                🚀 Войти в VetFormuLab
              </>
            )}
          </button>
        </form>

        {/* Ссылка на регистрацию */}
        <div style={{
          textAlign: 'center',
          marginTop: '32px',
          padding: '24px',
          background: 'rgba(76, 175, 80, 0.05)',
          borderRadius: '16px',
          border: '1px solid rgba(76, 175, 80, 0.1)'
        }}>
          <p style={{
            color: 'var(--text-secondary)',
            marginBottom: '12px',
            fontSize: '14px'
          }}>
            Впервые в VetFormuLab? 🆕
          </p>
          <Link
            to="/register"
            style={{
              color: 'var(--primary-green)',
              textDecoration: 'none',
              fontWeight: '600',
              display: 'inline-flex',
              alignItems: 'center',
              gap: '6px',
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.color = 'var(--dark-green)';
              e.currentTarget.style.transform = 'translateY(-1px)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.color = 'var(--primary-green)';
              e.currentTarget.style.transform = 'translateY(0)';
            }}
          >
            🏥 Зарегистрировать клинику
          </Link>
        </div>

        {/* Демо данные */}
        <div style={{
          marginTop: '20px',
          padding: '16px',
          background: 'rgba(255, 143, 0, 0.05)',
          borderRadius: '12px',
          border: '1px solid rgba(255, 143, 0, 0.2)',
          fontSize: '12px',
          color: 'var(--text-secondary)'
        }}>
          <div style={{ fontWeight: '600', marginBottom: '8px', color: 'var(--accent-orange)' }}>
            💡 Демо доступ:
          </div>
          <div>📧 demo@clinic.com</div>
          <div>🔒 demo123</div>
        </div>
      </div>

      {/* CSS анимации */}
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default Login;
