import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Dashboard: React.FC = () => {
  const { isAuthenticated } = useAuth();
  const [stats, setStats] = useState({
    feeds: 0,
    animals: 0,
    comparisons: 0,
    brands: 0
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchStats = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      // –í—Å–µ–≥–¥–∞ –∑–∞–≥—Ä—É–∂–∞–µ–º –ø—É–±–ª–∏—á–Ω—ã–µ –∫–æ—Ä–º–∞
      const feedsRes = await fetch('http://localhost:3001/api/feeds');

      if (!feedsRes.ok) {
        throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∫–æ—Ä–º–æ–≤');
      }

      const feeds = await feedsRes.json();
      const uniqueBrands = new Set(feeds.map((feed: any) => feed.brand));

      if (isAuthenticated) {
        // –î–ª—è –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –∑–∞–≥—Ä—É–∂–∞–µ–º –≤—Å–µ –¥–∞–Ω–Ω—ã–µ
        try {
          const token = localStorage.getItem('token');
          const requestOptions: RequestInit = token 
            ? { headers: { 'Authorization': `Bearer ${token}` } }
            : {};

          const [animalsRes, comparisonsRes] = await Promise.all([
            fetch('http://localhost:3001/api/animals', requestOptions),
            fetch('http://localhost:3001/api/calculations', requestOptions)
          ]);

          if (animalsRes.ok && comparisonsRes.ok) {
            const [animals, calculations] = await Promise.all([
              animalsRes.json(),
              comparisonsRes.json()
            ]);

            setStats({
              feeds: feeds.length,
              animals: animals.length,
              comparisons: calculations.length,
              brands: uniqueBrands.size
            });
          } else {
            setStats({
              feeds: feeds.length,
              animals: 0,
              comparisons: 0,
              brands: uniqueBrands.size
            });
          }
        } catch (authError) {
          console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞—â–∏—â–µ–Ω–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö:', authError);
          setStats({
            feeds: feeds.length,
            animals: 0,
            comparisons: 0,
            brands: uniqueBrands.size
          });
        }
      } else {
        // –î–ª—è –Ω–µ–∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–æ–ª—å–∫–æ –ø—É–±–ª–∏—á–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
        setStats({
          feeds: feeds.length,
          animals: 0,
          comparisons: 0,
          brands: uniqueBrands.size
        });
      }

    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏:', error);
      setError('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ');
    } finally {
      setLoading(false);
    }
  }, [isAuthenticated]);

  useEffect(() => {
    fetchStats();
  }, [fetchStats]);

  if (loading) {
    return (
      <div style={{ 
        textAlign: 'center', 
        padding: '50px',
        fontSize: '18px'
      }}>
        <div style={{ fontSize: '48px', marginBottom: '20px' }}>üêï</div>
