import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  PlusIcon, 
  UserGroupIcon, 
  BeakerIcon, 
  ChartBarIcon,
  CalculatorIcon,
  ArrowUpIcon,
  ArrowDownIcon
} from '@heroicons/react/24/outline';
import { feedsApi, calculationsApi, animalsApi } from '../services/api';
import { FeedStats, CalculationStats, Animal } from '../types';
import toast from 'react-hot-toast';

const Dashboard: React.FC = () => {
  const [feedStats, setFeedStats] = useState<FeedStats | null>(null);
  const [calcStats, setCalcStats] = useState<CalculationStats | null>(null);
  const [recentAnimals, setRecentAnimals] = useState<Animal[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadDashboardData = async () => {
      try {
        setLoading(true);

        // Загружаем статистику параллельно
        const [feedStatsRes, calcStatsRes, animalsRes] = await Promise.all([
          feedsApi.getStats().catch(() => null),
          calculationsApi.getStats().catch(() => null),
          animalsApi.getAll().catch(() => null),
        ]);

        if (feedStatsRes) {
          setFeedStats(feedStatsRes.data);
        }

        if (calcStatsRes) {
          setCalcStats(calcStatsRes.data);
        }

        if (animalsRes) {
          // Показываем последних 5 животных
          setRecentAnimals(animalsRes.data.slice(0, 5));
        }
      } catch (error) {
        console.error('Ошибка загрузки данных dashboard:', error);
        toast.error('Ошибка загрузки данных');
      } finally {
        setLoading(false);
      }
    };

    loadDashboardData();
  }, []);

  const quickActions = [
    {
      title: 'Добавить животное',
      description: 'Создать новый профиль животного',
      href: '/animals/new',
      icon: UserGroupIcon,
      color: 'bg-blue-500',
    },
    {
      title: 'Добавить корм',
      description: 'Добавить новый корм в базу',
      href: '/feeds/new',
      icon: BeakerIcon,
      color: 'bg-green-500',
    },
    {
      title: 'Сравнить корма',
      description: 'Сравнить различные корма',
      href: '/comparison',
      icon: ChartBarIcon,
      color: 'bg-purple-500',
    },
    {
      title: 'Калькулятор',
      description: 'Рассчитать потребности',
      href: '/calculator',
      icon: CalculatorIcon,
      color: 'bg-orange-500',
    },
  ];

  const statCards = [
    {
      title: 'Всего кормов',
      value: feedStats?.totalFeeds || 0,
      change: '+12%',
      changeType: 'increase' as const,
      icon: BeakerIcon,
    },
    {
      title: 'Животных',
      value: recentAnimals.length || 0,
      change: '+5%',
      changeType: 'increase' as const,
      icon: UserGroupIcon,
    },
    {
      title: 'Сравнений',
      value: calcStats?.totalComparisons || 0,
      change: '+8%',
      changeType: 'increase' as const,
      icon: ChartBarIcon,
    },
    {
      title: 'Брендов',
      value: feedStats?.totalBrands || 0,
      change: '+2%',
      changeType: 'increase' as const,
      icon: BeakerIcon,
    },
  ];

  if (loading) {
    return (
      <div className="px-4 sm:px-6 lg:px-8">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-8"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-32 bg-gray-200 rounded-lg"></div>
            ))}
          </div>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            <div className="h-64 bg-gray-200 rounded-lg"></div>
            <div className="h-64 bg-gray-200 rounded-lg"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="px-4 sm:px-6 lg:px-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Добро пожаловать в VetFormuLab</h1>
        <p className="mt-2 text-gray-600">
          Система для расчета и сравнения кормов для животных
        </p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {statCards.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div key={index} className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">{stat.title}</p>
                  <p className="text-3xl font-bold text-gray-900">{stat.value}</p>
                </div>
                <div className="p-3 bg-primary-50 rounded-lg">
                  <Icon className="h-6 w-6 text-primary-600" />
                </div>
              </div>
              <div className="mt-4 flex items-center">
                {stat.changeType === 'increase' ? (
                  <ArrowUpIcon className="h-4 w-4 text-success-500" />
                ) : (
                  <ArrowDownIcon className="h-4 w-4 text-danger-500" />
                )}
                <span className={`ml-1 text-sm font-medium ${
                  stat.changeType === 'increase' ? 'text-success-600' : 'text-danger-600'
                }`}>
                  {stat.change}
                </span>
                <span className="text-sm text-gray-500 ml-2">за месяц</span>
              </div>
            </div>
          );
        })}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Quick Actions */}
        <div className="card">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Быстрые действия</h2>
          <div className="space-y-3">
            {quickActions.map((action, index) => {
              const Icon = action.icon;
              return (
                <Link
                  key={index}
                  to={action.href}
                  className="flex items-center p-3 rounded-lg border border-gray-200 hover:border-primary-300 hover:bg-primary-25 transition-all duration-200 group"
                >
                  <div className={`p-2 rounded-lg ${action.color} bg-opacity-10 group-hover:bg-opacity-20 transition-all duration-200`}>
                    <Icon className={`h-5 w-5 ${action.color.replace('bg-', 'text-')}`} />
                  </div>
                  <div className="ml-3 flex-1">
                    <h3 className="text-sm font-medium text-gray-900">{action.title}</h3>
                    <p className="text-sm text-gray-500">{action.description}</p>
                  </div>
                  <PlusIcon className="h-5 w-5 text-gray-400 group-hover:text-primary-500 transition-colors duration-200" />
                </Link>
              );
            })}
          </div>
        </div>

        {/* Recent Animals */}
        <div className="card">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Последние животные</h2>
            <Link 
              to="/animals" 
              className="text-sm text-primary-600 hover:text-primary-700 font-medium"
            >
              Посмотреть все
            </Link>
          </div>

          {recentAnimals.length > 0 ? (
            <div className="space-y-3">
              {recentAnimals.map((animal) => (
                <Link
                  key={animal.id}
                  to={`/animals/${animal.id}`}
                  className="flex items-center p-3 rounded-lg border border-gray-200 hover:border-primary-300 hover:bg-primary-25 transition-all duration-200"
                >
                  <div className="flex-shrink-0">
                    <div className="w-10 h-10 bg-primary-100 rounded-full flex items-center justify-center">
                      <span className="text-lg">
                        {animal.species === 'dog' ? '🐕' : animal.species === 'cat' ? '🐱' : '🦔'}
                      </span>
                    </div>
                  </div>
                  <div className="ml-3 flex-1">
                    <h3 className="text-sm font-medium text-gray-900">{animal.name}</h3>
                    <p className="text-sm text-gray-500">
                      {animal.breed || 'Без породы'} • {animal.weight} кг
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-gray-500">
                      {animal.metabolic_energy_need ? `${animal.metabolic_energy_need} ккал/день` : 'Не рассчитано'}
                    </p>
                  </div>
                </Link>
              ))}
            </div>
          ) : (
            <div className="text-center py-6">
              <UserGroupIcon className="h-12 w-12 text-gray-400 mx-auto mb-3" />
              <p className="text-gray-500">Пока нет животных</p>
              <Link 
                to="/animals/new" 
                className="btn-primary btn-sm mt-3 inline-flex"
              >
                Добавить первое животное
              </Link>
            </div>
          )}
        </div>
      </div>

      {/* Feed Types Chart */}
      {feedStats?.feedTypes && feedStats.feedTypes.length > 0 && (
        <div className="mt-8">
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Распределение кормов по типам</h2>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {feedStats.feedTypes.map((type, index) => (
                <div key={index} className="text-center">
                  <div className="text-2xl font-bold text-primary-600">{type.count}</div>
                  <div className="text-sm text-gray-600 capitalize">{type.type}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
