import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  PlusIcon, 
  UserGroupIcon, 
  BeakerIcon, 
  ChartBarIcon,
  CalculatorIcon,
  ArrowUpIcon,
  ArrowDownIcon,
  SparklesIcon,
  FireIcon,
  StarIcon,
  TrophyIcon
} from '@heroicons/react/24/outline';
import { feedsApi, calculationsApi, animalsApi } from '../services/api';
import { FeedStats, CalculationStats, Animal } from '../types';
import toast from 'react-hot-toast';

const Dashboard: React.FC = () => {
  const [feedStats, setFeedStats] = useState<FeedStats | null>(null);
  const [calcStats, setCalcStats] = useState<CalculationStats | null>(null);
  const [recentAnimals, setRecentAnimals] = useState<Animal[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadDashboardData = async () => {
      try {
        setLoading(true);

        // Загружаем статистику параллельно
        const [feedStatsRes, calcStatsRes, animalsRes] = await Promise.all([
          feedsApi.getStats().catch(() => null),
          calculationsApi.getStats().catch(() => null),
          animalsApi.getAll().catch(() => null),
        ]);

        if (feedStatsRes) {
          setFeedStats(feedStatsRes.data);
        }

        if (calcStatsRes) {
          setCalcStats(calcStatsRes.data);
        }

        if (animalsRes) {
          setRecentAnimals(animalsRes.data.slice(0, 5));
        }
      } catch (error) {
        console.error('Ошибка загрузки данных dashboard:', error);
        toast.error('Ошибка загрузки данных');
      } finally {
        setLoading(false);
      }
    };

    loadDashboardData();
  }, []);

  const quickActions = [
    {
      title: 'Добавить животное',
      description: 'Создать профиль питомца',
      href: '/animals/new',
      icon: UserGroupIcon,
      gradient: 'var(--primary-gradient)',
      emoji: '🐕'
    },
    {
      title: 'Добавить корм',
      description: 'Пополнить базу кормов',
      href: '/feeds/new',
      icon: BeakerIcon,
      gradient: 'var(--success-gradient)',
      emoji: '🥘'
    },
    {
      title: 'Сравнить корма',
      description: 'Анализ питательности',
      href: '/comparison',
      icon: ChartBarIcon,
      gradient: 'var(--secondary-gradient)',
      emoji: '📊'
    },
    {
      title: 'Калькулятор',
      description: 'Рассчитать потребности',
      href: '/calculator',
      icon: CalculatorIcon,
      gradient: 'var(--warning-gradient)',
      emoji: '🧮'
    },
  ];

  const statCards = [
    {
      title: 'Всего кормов',
      value: feedStats?.totalFeeds || 0,
      change: '+12%',
      changeType: 'increase' as const,
      icon: BeakerIcon,
      gradient: 'var(--success-gradient)',
      emoji: '🥘'
    },
    {
      title: 'Животных',
      value: recentAnimals.length || 0,
      change: '+5%',
      changeType: 'increase' as const,
      icon: UserGroupIcon,
      gradient: 'var(--primary-gradient)',
      emoji: '🐾'
    },
    {
      title: 'Сравнений',
      value: calcStats?.totalComparisons || 0,
      change: '+8%',
      changeType: 'increase' as const,
      icon: ChartBarIcon,
      gradient: 'var(--secondary-gradient)',
      emoji: '📈'
    },
    {
      title: 'Брендов',
      value: feedStats?.totalBrands || 0,
      change: '+2%',
      changeType: 'increase' as const,
      icon: StarIcon,
      gradient: 'var(--warning-gradient)',
      emoji: '⭐'
    },
  ];

  if (loading) {
    return (
      <div style={{ padding: '2rem' }}>
        <div className="animate-pulse">
          <div style={{
            height: '3rem',
            background: 'rgba(255, 255, 255, 0.2)',
            borderRadius: '12px',
            width: '50%',
            marginBottom: '3rem'
          }}></div>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
            gap: '2rem',
            marginBottom: '3rem'
          }}>
            {[...Array(4)].map((_, i) => (
              <div key={i} style={{
                height: '150px',
                background: 'rgba(255, 255, 255, 0.2)',
                borderRadius: '20px'
              }}></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
      {/* Hero Header */}
      <div className="card-hero animate-slideUp" style={{ marginBottom: '3rem' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginBottom: '1.5rem'
        }}>
          <TrophyIcon style={{ width: '3rem', height: '3rem', marginRight: '1rem' }} />
          <h1 style={{
            fontSize: '3.5rem',
            fontWeight: 900,
            margin: 0,
            fontFamily: 'Poppins, sans-serif'
          }}>
            VetFormuLab Pro
          </h1>
          <SparklesIcon style={{ width: '3rem', height: '3rem', marginLeft: '1rem', animation: 'pulse 2s infinite' }} />
        </div>
        <p style={{
          fontSize: '1.4rem',
          fontWeight: 300,
          marginBottom: '2rem',
          opacity: 0.9,
          maxWidth: '600px',
          margin: '0 auto 2rem'
        }}>
          Революционная система расчета кормов с искусственным интеллектом
        </p>
        <div style={{
          display: 'flex',
          gap: '1rem',
          justifyContent: 'center',
          flexWrap: 'wrap'
        }}>
          <div className="badge" style={{
            background: 'rgba(255, 255, 255, 0.2)',
            color: 'white',
            padding: '0.5rem 1rem',
            borderRadius: '20px',
            fontWeight: 600
          }}>
            🚀 AI Powered
          </div>
          <div className="badge" style={{
            background: 'rgba(255, 255, 255, 0.2)',
            color: 'white',
            padding: '0.5rem 1rem',
            borderRadius: '20px',
            fontWeight: 600
          }}>
            🌍 Мировой стандарт
          </div>
          <div className="badge" style={{
            background: 'rgba(255, 255, 255, 0.2)',
            color: 'white',
            padding: '0.5rem 1rem',
            borderRadius: '20px',
            fontWeight: 600
          }}>
            ⚡ Мгновенные расчеты
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
        gap: '2rem',
        marginBottom: '3rem'
      }}>
        {statCards.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div 
              key={index} 
              className="card card-premium animate-slideUp" 
              style={{
                animationDelay: `${index * 0.1}s`,
                position: 'relative',
                overflow: 'hidden'
              }}
            >
              <div style={{
                position: 'absolute',
                top: '-50%',
                right: '-30%',
                width: '120px',
                height: '120px',
                background: stat.gradient,
                borderRadius: '50%',
                opacity: 0.1,
                animation: 'float 3s ease-in-out infinite'
              }} />

              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'space-between',
                marginBottom: '1.5rem'
              }}>
                <div>
                  <p style={{
                    fontSize: '0.9rem',
                    fontWeight: 600,
                    color: 'var(--text-light)',
                    marginBottom: '0.5rem',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    {stat.title}
                  </p>
                  <p style={{
                    fontSize: '2.5rem',
                    fontWeight: 800,
                    color: 'var(--text-dark)',
                    margin: 0,
                    fontFamily: 'Poppins, sans-serif'
                  }}>
                    {stat.value}
                  </p>
                </div>
                <div style={{
                  width: '60px',
                  height: '60px',
                  borderRadius: '16px',
                  background: stat.gradient,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '1.5rem',
                  animation: 'pulse 2s infinite'
                }}>
                  {stat.emoji}
                </div>
              </div>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                {stat.changeType === 'increase' ? (
                  <ArrowUpIcon style={{ width: '1rem', height: '1rem', color: '#10b981' }} />
                ) : (
                  <ArrowDownIcon style={{ width: '1rem', height: '1rem', color: '#ef4444' }} />
                )}
                <span style={{
                  marginLeft: '0.5rem',
                  fontSize: '0.9rem',
                  fontWeight: 600,
                  color: stat.changeType === 'increase' ? '#10b981' : '#ef4444'
                }}>
                  {stat.change}
                </span>
                <span style={{
                  fontSize: '0.9rem',
                  color: 'var(--text-light)',
                  marginLeft: '0.5rem'
                }}>
                  за месяц
                </span>
              </div>
            </div>
          );
        })}
      </div>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(500px, 1fr))',
        gap: '2rem'
      }}>
        {/* Quick Actions */}
        <div className="card card-premium animate-slideUp" style={{ animationDelay: '0.4s' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '2rem' }}>
            <FireIcon style={{ width: '1.5rem', height: '1.5rem', marginRight: '0.5rem', color: '#f59e0b' }} />
            <h2 style={{
              fontSize: '1.5rem',
              fontWeight: 700,
              color: 'var(--text-dark)',
              margin: 0
            }}>
              Быстрые действия
            </h2>
          </div>

          <div style={{ display: 'grid', gap: '1rem' }}>
            {quickActions.map((action, index) => {
              const Icon = action.icon;
              return (
                <Link
                  key={index}
                  to={action.href}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    padding: '1.5rem',
                    borderRadius: '16px',
                    border: '2px solid rgba(102, 126, 234, 0.1)',
                    textDecoration: 'none',
                    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                    background: 'rgba(255, 255, 255, 0.5)',
                    backdropFilter: 'blur(10px)',
                    position: 'relative',
                    overflow: 'hidden'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'translateY(-5px)';
                    e.currentTarget.style.boxShadow = 'var(--shadow-2xl)';
                    e.currentTarget.style.borderColor = 'rgba(102, 126, 234, 0.3)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = 'var(--shadow-lg)';
                    e.currentTarget.style.borderColor = 'rgba(102, 126, 234, 0.1)';
                  }}
                >
                  <div style={{
                    width: '50px',
                    height: '50px',
                    borderRadius: '12px',
                    background: action.gradient,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginRight: '1rem',
                    fontSize: '1.5rem'
                  }}>
                    {action.emoji}
                  </div>
                  <div style={{ flex: 1 }}>
                    <h3 style={{
                      fontSize: '1.1rem',
                      fontWeight: 600,
                      color: 'var(--text-dark)',
                      margin: '0 0 0.25rem 0'
                    }}>
                      {action.title}
                    </h3>
                    <p style={{
                      fontSize: '0.9rem',
                      color: 'var(--text-light)',
                      margin: 0
                    }}>
                      {action.description}
                    </p>
                  </div>
                  <PlusIcon style={{
                    width: '1.25rem',
                    height: '1.25rem',
                    color: 'var(--text-light)',
                    transition: 'all 0.3s'
                  }} />
                </Link>
              );
            })}
          </div>
        </div>

        {/* Recent Animals */}
        <div className="card card-premium animate-slideUp" style={{ animationDelay: '0.6s' }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            marginBottom: '2rem'
          }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <UserGroupIcon style={{ width: '1.5rem', height: '1.5rem', marginRight: '0.5rem', color: '#667eea' }} />
              <h2 style={{
                fontSize: '1.5rem',
                fontWeight: 700,
                color: 'var(--text-dark)',
                margin: 0
              }}>
                Последние животные
              </h2>
            </div>
            <Link 
              to="/animals" 
              className="btn btn-secondary"
              style={{
                textDecoration: 'none',
                fontSize: '0.8rem',
                padding: '0.5rem 1rem'
              }}
            >
              Все животные
            </Link>
          </div>

          {recentAnimals.length > 0 ? (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
              {recentAnimals.map((animal, index) => (
                <Link
                  key={animal.id}
                  to={`/animals/${animal.id}`}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    padding: '1rem',
                    borderRadius: '12px',
                    border: '1px solid rgba(102, 126, 234, 0.1)',
                    textDecoration: 'none',
                    transition: 'all 0.3s',
                    background: 'rgba(255, 255, 255, 0.5)',
                    animationDelay: `${index * 0.1}s`
                  }}
                  className="animate-slideUp"
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'translateX(5px)';
                    e.currentTarget.style.background = 'rgba(102, 126, 234, 0.05)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'translateX(0)';
                    e.currentTarget.style.background = 'rgba(255, 255, 255, 0.5)';
                  }}
                >
                  <div style={{
                    width: '50px',
                    height: '50px',
                    borderRadius: '50%',
                    background: 'var(--primary-gradient)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '1.5rem',
                    marginRight: '1rem'
                  }}>
                    {animal.species === 'dog' ? '🐕' : animal.species === 'cat' ? '🐱' : '🦔'}
                  </div>
                  <div style={{ flex: 1 }}>
                    <h3 style={{
                      fontSize: '1.1rem',
                      fontWeight: 600,
                      color: 'var(--text-dark)',
                      margin: '0 0 0.25rem 0'
                    }}>
                      {animal.name}
                    </h3>
                    <p style={{
                      fontSize: '0.9rem',
                      color: 'var(--text-light)',
                      margin: 0
                    }}>
                      {animal.breed || 'Без породы'} • {animal.weight} кг
                    </p>
                  </div>
                  <div style={{ textAlign: 'right' }}>
                    <div style={{
                      fontSize: '0.8rem',
                      fontWeight: 600,
                      color: 'var(--text-dark)',
                      marginBottom: '0.25rem'
                    }}>
                      {animal.metabolic_energy_need || 'Не рассчитано'}
                    </div>
                    <div style={{
                      fontSize: '0.7rem',
                      color: 'var(--text-light)'
                    }}>
                      ккал/день
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          ) : (
            <div style={{
              textAlign: 'center',
              padding: '3rem 1rem',
              background: 'rgba(102, 126, 234, 0.05)',
              borderRadius: '16px',
              border: '2px dashed rgba(102, 126, 234, 0.2)'
            }}>
              <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>🐾</div>
              <h3 style={{
                fontSize: '1.2rem',
                fontWeight: 600,
                color: 'var(--text-dark)',
                marginBottom: '0.5rem'
              }}>
                Пока нет животных
              </h3>
              <p style={{
                color: 'var(--text-light)',
                marginBottom: '1.5rem'
              }}>
                Добавьте первого питомца для начала работы
              </p>
              <Link 
                to="/animals/new" 
                className="btn btn-primary"
                style={{ textDecoration: 'none' }}
              >
                🐕 Добавить животное
              </Link>
            </div>
          )}
        </div>
      </div>

      {/* Feed Types Chart */}
      {feedStats?.feedTypes && feedStats.feedTypes.length > 0 && (
        <div style={{ marginTop: '3rem' }}>
          <div className="card card-premium animate-slideUp" style={{ animationDelay: '0.8s' }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '2rem' }}>
              <ChartBarIcon style={{ width: '1.5rem', height: '1.5rem', marginRight: '0.5rem', color: '#8b5cf6' }} />
              <h2 style={{
                fontSize: '1.5rem',
                fontWeight: 700,
                color: 'var(--text-dark)',
                margin: 0
              }}>
                Статистика кормов
              </h2>
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
              gap: '2rem'
            }}>
              {feedStats.feedTypes.map((type, index) => (
                <div 
                  key={index} 
                  style={{
                    textAlign: 'center',
                    padding: '1.5rem',
                    borderRadius: '16px',
                    background: 'var(--primary-gradient)',
                    color: 'white',
                    animation: 'float 3s ease-in-out infinite',
                    animationDelay: `${index * 0.2}s`
                  }}
                >
                  <div style={{
                    fontSize: '2.5rem',
                    fontWeight: 800,
                    marginBottom: '0.5rem',
                    fontFamily: 'Poppins, sans-serif'
                  }}>
                    {type.count}
                  </div>
                  <div style={{
                    fontSize: '1rem',
                    fontWeight: 500,
                    textTransform: 'capitalize',
                    opacity: 0.9
                  }}>
                    {type.type === 'dry' ? '🥘 Сухой' : 
                     type.type === 'wet' ? '🥫 Влажный' : 
                     type.type === 'raw' ? '🥩 Сырой' : '🍪 Лакомства'}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
