import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Dashboard: React.FC = () => {
  const { isAuthenticated } = useAuth();
  const [stats, setStats] = useState({
    feeds: 0,
    animals: 0,
    comparisons: 0,
    brands: 0
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      setLoading(true);
      setError(null);

      // Всегда загружаем публичные корма
      const feedsRes = await fetch('http://localhost:3001/api/feeds');

      if (!feedsRes.ok) {
        throw new Error('Ошибка загрузки кормов');
      }

      const feeds = await feedsRes.json();
      const uniqueBrands = new Set(feeds.map((feed: any) => feed.brand));

      if (isAuthenticated) {
        // Для авторизованных пользователей загружаем все данные
        try {
          const token = localStorage.getItem('token');
          const requestOptions: RequestInit = token 
            ? { headers: { 'Authorization': `Bearer ${token}` } }
            : {};

          const [animalsRes, comparisonsRes] = await Promise.all([
            fetch('http://localhost:3001/api/animals', requestOptions),
            fetch('http://localhost:3001/api/calculations', requestOptions)
          ]);

          if (animalsRes.ok && comparisonsRes.ok) {
            const [animals, calculations] = await Promise.all([
              animalsRes.json(),
              comparisonsRes.json()
            ]);

            setStats({
              feeds: feeds.length,
              animals: animals.length,
              comparisons: calculations.length,
              brands: uniqueBrands.size
            });
          } else {
            setStats({
              feeds: feeds.length,
              animals: 0,
              comparisons: 0,
              brands: uniqueBrands.size
            });
          }
        } catch (authError) {
          console.error('Ошибка загрузки защищенных данных:', authError);
          setStats({
            feeds: feeds.length,
            animals: 0,
            comparisons: 0,
            brands: uniqueBrands.size
          });
        }
      } else {
        // Для неавторизованных пользователей показываем только публичные данные
        setStats({
          feeds: feeds.length,
          animals: 0,
          comparisons: 0,
          brands: uniqueBrands.size
        });
      }

    } catch (error) {
      console.error('Ошибка загрузки статистики:', error);
      setError('Не удалось загрузить данные');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{ 
        textAlign: 'center', 
        padding: '50px',
        fontSize: '18px'
      }}>
        <div style={{ fontSize: '48px', marginBottom: '20px' }}>🐕</div>
        <div>Загружаем данные...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        textAlign: 'center', 
        padding: '50px',
        color: '#F44336',
        background: 'rgba(244, 67, 54, 0.1)',
        borderRadius: '16px',
        margin: '20px'
      }}>
        <div style={{ fontSize: '48px', marginBottom: '20px' }}>⚠️</div>
        <h3>Ошибка загрузки</h3>
        <p>{error}</p>
        <button 
          onClick={fetchStats}
          style={{
            padding: '12px 24px',
            background: '#00C851',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            marginTop: '20px'
          }}
        >
          Попробовать снова
        </button>
      </div>
    );
  }

  return (
    <div style={{ 
      padding: window.innerWidth <= 768 ? '10px' : '20px',
      maxWidth: '1200px',
      margin: '0 auto'
    }}>
      {/* Hero блок */}
      <div style={{
        textAlign: 'center',
        marginBottom: window.innerWidth <= 768 ? '40px' : '60px',
        padding: window.innerWidth <= 768 ? '40px 15px' : '80px 20px',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: window.innerWidth <= 768 ? '20px' : '32px',
        color: 'white'
      }}>
        <div style={{ fontSize: '80px', marginBottom: '30px' }}>🏥🐾</div>

        <h1 style={{
          fontSize: '4rem',
          fontWeight: '900',
          marginBottom: '24px',
          color: 'white'
        }}>
          VetFormuLab Pro
        </h1>

        <p style={{
          fontSize: '1.5rem',
          marginBottom: '40px',
          maxWidth: '700px',
          margin: '0 auto 40px auto',
          opacity: 0.95
        }}>
          Научные расчеты питания для профессиональных ветеринаров
        </p>

        <Link 
          to="/calculator"
          style={{
            display: 'inline-flex',
            alignItems: 'center',
            gap: '12px',
            background: 'rgba(255, 255, 255, 0.95)',
            color: '#667eea',
            fontSize: '1.3rem',
            fontWeight: '700',
            padding: '20px 40px',
            borderRadius: '25px',
            textDecoration: 'none',
            transition: 'all 0.3s ease',
            boxShadow: '0 15px 35px rgba(0,0,0,0.2)'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = 'translateY(-5px) scale(1.05)';
            e.currentTarget.style.boxShadow = '0 25px 50px rgba(0,0,0,0.3)';
            e.currentTarget.style.background = 'rgba(255, 255, 255, 1)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = 'translateY(0) scale(1)';
            e.currentTarget.style.boxShadow = '0 15px 35px rgba(0,0,0,0.2)';
            e.currentTarget.style.background = 'rgba(255, 255, 255, 0.95)';
          }}
          onMouseDown={(e) => {
            e.currentTarget.style.transform = 'translateY(-2px) scale(1.02)';
          }}
          onMouseUp={(e) => {
            e.currentTarget.style.transform = 'translateY(-5px) scale(1.05)';
          }}
        >
          🧮 Попробовать бесплатно
        </Link>
      </div>

      {/* Карточки преимуществ */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: '30px',
        marginBottom: '60px'
      }}>
        <div 
          style={{
            background: 'rgba(255, 255, 255, 0.95)',
            borderRadius: '20px',
            padding: '30px',
            boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
            border: '2px solid rgba(0, 200, 81, 0.2)',
            textAlign: 'center',
            transition: 'all 0.3s ease',
            cursor: 'pointer'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = 'translateY(-8px) scale(1.02)';
            e.currentTarget.style.boxShadow = '0 20px 60px rgba(0, 200, 81, 0.15)';
            e.currentTarget.style.border = '2px solid rgba(0, 200, 81, 0.4)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = 'translateY(0) scale(1)';
