import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';

// Импорт компонентов
import Layout from './components/Layout/Layout';
import {
  Dashboard,
  Animals,
  AnimalForm,
  AnimalDetail,
  Feeds,
  FeedForm,
  FeedDetail,
  Comparison,
  Calculator
} from './pages';

function App() {
  return (
    <div className="App">
      <Router>
        <Layout>
          <Routes>
            {/* Главная страница - Dashboard */}
            <Route path="/" element={<Dashboard />} />

            {/* Животные */}
            <Route path="/animals" element={<Animals />} />
            <Route path="/animals/new" element={<AnimalForm />} />
            <Route path="/animals/:id" element={<AnimalDetail />} />
            <Route path="/animals/:id/edit" element={<AnimalForm />} />

            {/* Корма */}
            <Route path="/feeds" element={<Feeds />} />
            <Route path="/feeds/new" element={<FeedForm />} />
            <Route path="/feeds/:id" element={<FeedDetail />} />
            <Route path="/feeds/:id/edit" element={<FeedForm />} />

            {/* Сравнение и расчеты */}
            <Route path="/comparison" element={<Comparison />} />
            <Route path="/calculator" element={<Calculator />} />

            {/* 404 страница */}
            <Route path="*" element={
              <div className="min-h-screen flex items-center justify-center" style={{backgroundColor: '#f9fafb'}}>
                <div className="text-center">
                  <h1 className="text-6xl font-bold mb-4" style={{color: '#111827'}}>404</h1>
                  <p className="text-xl mb-8" style={{color: '#6b7280'}}>Страница не найдена</p>
                  <a
                    href="/"
                    className="btn-primary"
                  >
                    Вернуться на главную
                  </a>
                </div>
              </div>
            } />
          </Routes>
        </Layout>

        {/* Глобальные уведомления */}
        <Toaster
          position="top-right"
          toastOptions={{
            duration: 4000,
            style: {
              background: '#fff',
              color: '#374151',
              boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
              border: '1px solid #e5e7eb',
              borderRadius: '0.75rem',
            },
            success: {
              iconTheme: {
                primary: '#10b981',
                secondary: '#fff',
              },
            },
            error: {
              iconTheme: {
                primary: '#ef4444',
                secondary: '#fff',
              },
            },
          }}
        />
      </Router>
    </div>
  );
}

export default App;
